import React, { useState, useEffect } from "react";

const App = () => {

	// Declara uma variável no state [valor, funcao para alteração do valor]
	const [year, setYear] = useState(2022);
	const [month, setMonth] = useState('Março');

	// Executa após renderização
	useEffect(() => {
		document.title = `${month} de ${year}`
	}, [month, year]); // Monitora as alterações nessas states

	return (
		<>
			<h1>Return App JS</h1>
			<p>Bem-vindo a {month} de {year}</p>
		</>
	)
}

export default App;
