	const [month, setMonth] = useState('Março');

	// Executa após renderização
	useEffect(() => {
		console.log('useEffect');

		setYear(2023);
	});

	return (
		<>
			<h1>Return App JS</h1>
			<p>Bem-vindo ao ano de {year}</p>
		</>
	)
}

export default App;
