import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";

import HeaderPage from "./Components/Header/HeaderPage";

import Home from "./Pages/Geral/Home";
import About from "./Pages/Geral/About";
import Register from "./Pages/Geral/Register";
import Login from "./Pages/Geral/Login";
import Logout from "./Pages/Geral/Logout";

// import MyPosts from "./Pages/System/MyPosts";
import Posts from "./Pages/System/Posts";
import NewPost from "./Pages/System/NewPost";

const App = () => {

	const [logged, setLogged] = useState(true);

	let token = localStorage.getItem("token");

	if (token === undefined) {
		localStorage.removeItem("token");
		token = null;
	}

	useEffect(() => {
		const checkLogin = () => {
			if (token) {
				axios({
					method: "POST",
					url: `http://localhost:8000/api/auth/validate`,
					data: { token }
				}).then((success) => {
					if (success.data.success) {
						setLogged(true);
					} else {
						setLogged(false);
						localStorage.removeItem("token");
						console.log("UE", success);
						let local = window.location.href;
						window.location.href = local;
					}
				}).catch(error => {
					setLogged(false);
					localStorage.removeItem("token");
					console.log("UE", error);
					let local = window.location.href;
					window.location.href = local;
				})
			} else {
				setLogged(false);
                localStorage.removeItem("token");
            }
		}

		checkLogin();
	}, [token]);

	const systemRoute = props => {
		if (logged) {
			console.log(1)
			// return props;
		} else if (token) {
			axios({
				method: "POST",
				url: `http://localhost:8000/api/auth/validate`,
				data: { token }
			}).then((success) => {
				if (success.data.success) {
					console.log(2);
					// return props;
				} else {
					console.log("SR", success);
					// return <Navigate to="logout"/>
				}
			}).catch(error => {
				console.log("SR", error);
				// return <Navigate to="/logout" />
			})
		} else {
			console.log('oi');
			// return <Navigate to="/logout" />
		}
	}

	return (
		<>
			<BrowserRouter>

				<HeaderPage logged={logged} />

                <Routes>

					{/* Tela Inicial */}
                    <Route path="/" element={<Home/>} />
					<Route path="/sobre" element={<About/>} />
					<Route path="/cadastro" element={<Register/>} />
					<Route path="/login" element={<Login/>} />
					<Route path="/logout" element={<Logout/>} />

					{/* System */}
					{/* <Route path="/posts/meus" element={<MyPosts/>} /> */}
					<Route path="/posts" element={systemRoute(<Posts/>)} />
					<Route path="/posts/novo" element={systemRoute(<NewPost/>)} />

				</Routes>

            </BrowserRouter>
		</>
	)

}

export default App;
